import React, { useState } from 'react';
import { Dialog, DialogContent, IconButton, CircularProgress, Box, Typography } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

const PdfViewer = ({ pdfContent, open, onClose }) => {
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const handleIframeLoad = () => {
    setIsLoading(false);
  };

  const handleIframeError = () => {
    setError('Failed to load PDF');
    setIsLoading(false);
  };

  return (
    <Dialog
      open={open}
      onClose={onClose}
      maxWidth="lg"
      fullWidth
      PaperProps={{
        sx: {
          height: '90vh',
          bgcolor: 'background.paper',
        }
      }}
    >
      <IconButton
        onClick={onClose}
        sx={{
          position: 'absolute',
          right: 8,
          top: 8,
          color: 'grey.500',
          zIndex: 1,
        }}
      >
        <CloseIcon />
      </IconButton>
      <DialogContent sx={{ p: 0, height: '100%', position: 'relative' }}>
        {isLoading && (
          <Box
            sx={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              textAlign: 'center',
            }}
          >
            <CircularProgress />
            <Typography sx={{ mt: 2 }}>Loading PDF...</Typography>
          </Box>
        )}

        {error && (
          <Box
            sx={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              textAlign: 'center',
              color: 'error.main',
            }}
          >
            <Typography>{error}</Typography>
          </Box>
        )}

        {pdfContent && (
          <object
            data={`data:application/pdf;base64,${pdfContent}`}
            type="application/pdf"
            width="100%"
            height="100%"
            onLoad={handleIframeLoad}
            onError={handleIframeError}
          >
            <embed
              src={`data:application/pdf;base64,${pdfContent}`}
              type="application/pdf"
              width="100%"
              height="100%"
            />
          </object>
        )}
      </DialogContent>
    </Dialog>
  );
};

export default PdfViewer;
